import json
import requests
from os import environ
from dotenv import load_dotenv
apikey=environ.get('etherscan_api_key')


def get_abi(contract_address):
    ret = requests.get(f'http://api.etherscan.io/api?module=contract&action=getabi&address=${contract_address}&apikey=${apikey}')
    if ret.status_code == 200:
        return ret.json()


